$(document).ready(function(){
    $("#dropdown").click(function(){
        $("#dropdown").toggleClass('drop2');
        $(".dropdown-content").slideToggle('medium');
    });
    $("#dropslide").click(function(){
        $(".dropslide").toggleClass("show");
        $("#dropdown").removeClass('drop2');
        $(".dropdown-content").slideUp('medium');
    });
    $("#login").click(function(){
        $("#log").slideDown('medium');
    });
    $("#signup").click(function(){
        $("#sign").slideDown('medium');
    });
    $("#name").click(function(){
        $("#first").addClass("under");
        $("#second").removeClass("under");
    });
    $("#password").click(function(){
        $("#second").addClass("under");
        $("#first").removeClass("under");
    });
    });
